examples.code:
# Get wildcard certificate for all subdomains
*.example.com {
    tls {
        dns cloudflare {env.CLOUDFLARE_API_TOKEN}
    }

    # Your site configuration
    reverse_proxy localhost:3000
}

{
    # Store certificates in Redis for cluster deployments
    storage redis {
        host localhost:6379
        password {env.REDIS_PASSWORD}
        db 0
        key_prefix "caddy"
    }
}

example.com {
    reverse_proxy localhost:8080
}

example.com {
    # Enable both Brotli and Gzip compression
    encode {
        brotli 6  # Compression level (0-11)
        gzip 6    # Fallback for older browsers
    }

    file_server
    root * /var/www/html
}

{
    # Use PostgreSQL for certificate storage
    storage postgres {
        host localhost
        port 5432
        database caddy
        user caddy
        password {env.POSTGRES_PASSWORD}
        table_name certificates
    }
}

example.com {
    reverse_proxy localhost:3000
}
